{
  "name": "Updated Test Role",
  "description": "Updated test role for API testing",
  "permissions": ["staff.view", "staff.create", "staff.update", "payroll.view"]
}

{
  "name": "Leave Request Form",
  "description": "Form for requesting leave",
  "form_type": "leave_request",
  "branch_id": 1,
  "fields": [
    {
      "field_name": "leave_type",
      "field_label": "Leave Type",
      "field_type": "dropdown",
      "is_required": true,
      "options": [
        { "label": "Annual Leave", "value": "annual" },
        { "label": "Sick Leave", "value": "sick" }
      ],
      "field_order": 1
    },
    {
      "field_name": "start_date",
      "field_label": "Start Date",
      "field_type": "date",
      "is_required": true,
      "field_order": 2
    },
    {
      "field_name": "reason",
      "field_label": "Reason",
      "field_type": "textarea",
      "is_required": false,
      "field_order": 3
    }
  ]
}

