}

{
  "name": "Updated Leave Request Form",
  "description": "Updated form for requesting leave",
  "fields": [
    {
      "field_name": "leave_type",
      "field_label": "Type of Leave",
      "field_type": "select",
      "is_required": true,
      "options": ["Annual", "Sick", "Casual", "Maternity"],
      "field_order": 1
    },
    {
      "field_name": "start_date",
      "field_label": "Start Date",
      "field_type": "date",
      "is_required": true,
      "field_order": 2
    },
    {
      "field_name": "end_date",
      "field_label": "End Date",
      "field_type": "date",
      "is_required": true,
      "field_order": 3
    },
    {
      "field_name": "reason",
      "field_label": "Reason for Leave",
      "field_type": "textarea",
      "is_required": true,
      "field_order": 4
    },
    {
      "field_name": "attachments",
      "field_label": "Supporting Documents",
      "field_type": "file",
      "is_required": false,
      "field_order": 5
    }
  ]
}

